CREATE DATABASE LibraryManagementSystem

USE LibraryManagementSystem


CREATE TABLE [User] (
	Id INT IDENTITY,
	Username VARCHAR(50),
	[Password] VARCHAR(50),
	Name VARCHAR(50),
	Email VARCHAR(50),
	Salt VARCHAR(50)
);




CREATE TABLE Book (
	Id INT IDENTITY,
	Title VARCHAR(50),
	Author VARCHAR(50),
	[Description] Text
);



CREATE TABLE Library (
	Id INT IDENTITY,
	Name VARCHAR(50),
	Location VARCHAR(50)
);

